import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import './App.css'; // Import CSS file for styling

function App() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [occupation, setOccupation] = useState('');
  const [city, setCity] = useState('');
  const [education, setEducation] = useState('');
  const [fatherName, setFatherName] = useState('');
  const [motherName, setMotherName] = useState('');
  const [dob, setDob] = useState('');
  const [birthTime, setBirthTime] = useState('');
  const [birthPlace, setBirthPlace] = useState('');
  const [gotra, setGotra] = useState('');
  const [nakshatra, setNakshatra] = useState('');
  const [photo, setPhoto] = useState(null);
  const [bioData, setBioData] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Assuming photo is uploaded and stored separately, we just use the name here
    const photoUrl = photo ? photo.name : '';
    const data = {
      name,
      age,
      gender,
      occupation,
      city,
      education,
      fatherName,
      motherName,
      dob,
      birthTime,
      birthPlace,
      gotra,
      nakshatra,
      photoUrl
    };
    setBioData(data);
  };

  return (
    <Container>
      <h1>Biodata Generator for Marriage</h1>
      <Form onSubmit={handleSubmit} className="custom-form">
        {/* Personal Details */}
        <h2>Personal Details</h2>
        <Form.Group controlId="name">
          <Form.Label>नाव (Name)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमचे पुर्ण नाव प्रविष्ट करा"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="age">
          <Form.Label>वय (Age)</Form.Label>
          <Form.Control
            type="number"
            placeholder="तुमचे वय प्रविष्ट करा"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="gender">
          <Form.Label>लिंग (Gender)</Form.Label>
          <Form.Control
            as="select"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          >
            <option value="">लिंग निवडा</option>
            <option value="male">पुरुष</option>
            <option value="female">स्त्री</option>
          </Form.Control>
        </Form.Group>

        {/* Education and Occupation */}
        <h2>Education and Occupation</h2>
        <Form.Group controlId="occupation">
          <Form.Label>व्यवसाय (Occupation)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमचा व्यवसाय प्रविष्ट करा"
            value={occupation}
            onChange={(e) => setOccupation(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="city">
          <Form.Label>शहर (City)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमचे शहर प्रविष्ट करा"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="education">
          <Form.Label>शिक्षण (Education)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमचा शिक्षण प्रविष्ट करा"
            value={education}
            onChange={(e) => setEducation(e.target.value)}
          />
        </Form.Group>

        {/* Family Details */}
        <h2>Family Details</h2>
        <Form.Group controlId="fatherName">
          <Form.Label>वडीलांचे नाव (Father's Name)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमच्या वडीलांचे पुर्ण नाव प्रविष्ट करा"
            value={fatherName}
            onChange={(e) => setFatherName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="motherName">
          <Form.Label>आईचे नाव (Mother's Name)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमच्या आईचे पुर्ण नाव प्रविष्ट करा"
            value={motherName}
            onChange={(e) => setMotherName(e.target.value)}
          />
        </Form.Group>

        {/* Birth Details */}
        <h2>Birth Details</h2>
        <Form.Group controlId="dob">
          <Form.Label>जन्म तारीख (Date of Birth)</Form.Label>
          <Form.Control
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="birthTime">
          <Form.Label>जन्म वेळ (Birth Time)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमच्या जन्माचे वेळ प्रविष्ट करा"
            value={birthTime}
            onChange={(e) => setBirthTime(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="birthPlace">
          <Form.Label>जन्म ठिकाण (Birth Place)</Form.Label>
          <Form.Control
            type="text"
            placeholder="तुमच्या जन्माचा ठिकाण प्रविष्ट करा"
            value={birthPlace}
            onChange={(e) => setBirthPlace(e.target.value)}
          />
        </Form.Group>

        {/* Astrology Details */}
        <h2>Astrology Details</h2>
        <Form.Group controlId="gotra">
        <Form.Label>गोत्र (Gotra)</Form.Label>
        <Form.Control
        type="text"
        placeholder="तुमचे गोत्र प्रविष्ट करा"
        value={gotra}
        onChange={(e) => setGotra(e.target.value)}
        />
        </Form.Group>
        <Form.Group controlId="nakshatra">
      <Form.Label>नक्षत्र (Nakshatra)</Form.Label>
      <Form.Control
        type="text"
        placeholder="तुमचे नक्षत्र प्रविष्ट करा"
        value={nakshatra}
        onChange={(e) => setNakshatra(e.target.value)}
      />
    </Form.Group>

    {/* Photo */}
    <h2>फोटो (Photo)</h2>
    <Form.Group controlId="photo">
      <Form.Label>तुमची फोटो अपलोड करा (Upload Your Photo)</Form.Label>
      <Form.Control
        type="file"
        onChange={(e) => setPhoto(e.target.files[0])}
      />
    </Form.Group>

    <Button variant="primary" type="submit">
      बायोडाटा तयार करा (Generate Biodata)
    </Button>
  </Form>

  {bioData && (
    <div className="biodata">
      <h2>तुमचा बायोडाटा (Your Biodata)</h2>
      <p>नाव (Name): {bioData.name}</p>
      <p>वय (Age): {bioData.age}</p>
      <p>लिंग (Gender): {bioData.gender}</p>
      <p>व्यवसाय (Occupation): {bioData.occupation}</p>
      <p>शहर (City): {bioData.city}</p>
      <p>शिक्षण (Education): {bioData.education}</p>
      <p>वडीलांचे नाव (Father's Name): {bioData.fatherName}</p>
      <p>आईचे नाव (Mother's Name): {bioData.motherName}</p>
      <p>जन्म तारीख (Date of Birth): {bioData.dob}</p>
      <p>जन्म वेळ (Birth Time): {bioData.birthTime}</p>
      <p>जन्म ठिकाण (Birth Place): {bioData.birthPlace}</p>
      <p>गोत्र (Gotra): {bioData.gotra}</p>
      <p>नक्षत्र (Nakshatra): {bioData.nakshatra}</p>
      <p>फोटो URL: {bioData.photoUrl}</p>
    </div>
  )}
</Container>
);
}


export default App;
